	static class Scanner {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		StringTokenizer st = new StringTokenizer("");

		public String nextLine() {
			try {
				return br.readLine();
			} catch (Exception e) {
				throw (new RuntimeException());
			}
		}

		public String next() {
			while (!st.hasMoreTokens()) {
				String l = nextLine();
				if (l == null)
					return null;
				st = new StringTokenizer(l);
			}
			return st.nextToken();
		}

		public int nextInt() {
			return Integer.parseInt(next());
		}

		public long nextLong() {
			return Long.parseLong(next());
		}

		public double nextDouble() {
			return Double.parseDouble(next());
		}

		public int[] nextIntArray(int n) {
			int[] res = new int[n];
			for (int i = 0; i < res.length; i++)
				res[i] = nextInt();
			return res;
		}

		public long[] nextLongArray(int n) {
			long[] res = new long[n];
			for (int i = 0; i < res.length; i++)
				res[i] = nextLong();
			return res;
		}

		public double[] nextDoubleArray(int n) {
			double[] res = new double[n];
			for (int i = 0; i < res.length; i++)
				res[i] = nextLong();
			return res;
		}

		public void sortIntArray(int[] array) {
			Integer[] vals = new Integer[array.length];
			for (int i = 0; i < array.length; i++)
				vals[i] = array[i];
			Arrays.sort(vals);
			for (int i = 0; i < array.length; i++)
				array[i] = vals[i];
		}

		public void sortLongArray(long[] array) {
			Long[] vals = new Long[array.length];
			for (int i = 0; i < array.length; i++)
				vals[i] = array[i];
			Arrays.sort(vals);
			for (int i = 0; i < array.length; i++)
				array[i] = vals[i];
		}

		public void sortDoubleArray(double[] array) {
			Double[] vals = new Double[array.length];
			for (int i = 0; i < array.length; i++)
				vals[i] = array[i];
			Arrays.sort(vals);
			for (int i = 0; i < array.length; i++)
				array[i] = vals[i];
		}
	}
	
	static int K;

	static long[] vacio = new long[]{0L, 0L};
	static long[][][][] dp;
	
	static long[] dp(int n, int k, int clicks)
	{
		if(k < 0)
			return vacio;
		if(dp[n][k][clicks][0] != -1)
			return dp[n][k][clicks];
		boolean prendido = (clicks & 1) == 1;
		if(n == 0)
		{
			if(k < K)
			{
				dp[n][k][clicks][0] = 0;
				dp[n][k][clicks][1] = 0;
				return dp[n][k][clicks];
			}
			else
			{
				dp[n][k][clicks][0] = clicks > k ? 1L : 0L;
				dp[n][k][clicks][1] = clicks < k ? 1L : 0L;
				return dp[n][k][clicks];
			}
		}
		long[] ans = dp[n][k][clicks];
		ans[0] = 0;
		ans[1] = 0;
		if(prendido)
		{
			long[] a = dp(n - 1, k, clicks + 1);
			ans[0] += a[0];
			ans[1] += a[1];
			a = dp(n - 1, k + 1, clicks);
			ans[0] += a[0];
			ans[1] += a[1];
		}
		else
		{
			long[] a = dp(n - 1, k, clicks);
			ans[0] += a[0];
			ans[1] += a[1];
			a = dp(n - 1, k + 1, clicks + 1);
			ans[0] += a[0];
			ans[1] += a[1];
		}
		return ans;
	}
	
