	public static void generar()
	{
		System.out.print("long[][][] dp1 = new long[][][]{");
		for(int i = 16; i < 64; i++)
		{
			System.out.print("{");
			dp = new long[64][64][64][];
			K = i;
			for(int j = i; j < 64; j++)
			{
				long[] resultado = dp(j, 0, 0);
				System.out.print("{" + resultado[0] + "L," + resultado[1] + "L},");
			}
			System.out.println("},");
		}
		System.out.println("};");
	}
	
	
	public static void main(String[] args)
	{
		Scanner sc = new Scanner();
		//generar();
		ArrayList <int[]> entradasA = new ArrayList <int[]> ();
		while(true)
		{
			int[] e = new int[]{sc.nextInt(), sc.nextInt()};
			if(e[0] == 0 && e[1] == 0)
				break;
			entradasA.add(e);
		}
		int[][] entradas = entradasA.toArray(new int[0][]);
		long[][] resultados = new long[entradas.length][];
		for(int i = 0; i < 64; i++)
		{
			boolean alguno = false;
			for(int[] e : entradas)
				if(e[1] == i)
					alguno = true;
			if(alguno)
			{
				if(i < 16)
				{
					dp = new long[64][64][64][2];
					for(long[][][] a : dp)
						for(long[][] b : a)
							for(long[] c : b)
								Arrays.fill(c, -1);
				}
				K = i;
				for(int j = 0; j < entradas.length; j++)
				{
					if(entradas[j][1] == i)
						if(i >= 16)
							resultados[j] = dp1[i - 16][entradas[j][0] - i];
						else
							resultados[j] = dp(entradas[j][0], 0, 0);
							
				}
			}
		}
		for(int i = 0; i < entradas.length; i++)
			System.out.println("Case " + (i + 1) + ": " + resultados[i][1] + " " + resultados[i][0]);
	}
